import React, {Component} from 'react';
import axios from 'axios';

class SignUp extends Component 
{
    constructor()
    {
        super()
        this.state = 
        {
            name: '',
            lastName: '',
            sex: '',
            email: '',
            password: ''
        }
        this.changeName = this.changeName.bind(this);
        this.changeLastName = this.changeLastName.bind(this);
        this.changeSex = this.changeSex.bind(this);
        this.changeEmail = this.changeEmail.bind(this);
        this.changePassword = this.changePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    changeName(e) { this.setState({ name: e.target.value }) }
    changeLastName(e) { this.setState({ lastName: e.target.value }) }
    changeSex(e) { this.setState({ sex: e.target.value }) }
    changeEmail(e) { this.setState({ email: e.target.value }) }
    changePassword(e) { this.setState({ password: e.target.value }) }

    onSubmit(e) 
    {
        e.preventDefault();

        const newUser = 
        {
            name: this.state.name,
            lastName: this.state.lastName,
            sex: this.state.sex,
            email: this.state.email,
            password: this.state.password
        }

        axios.post('http://localhost:4000/user/signup', newUser)
             .then(res => { window.alert(res.data.message); });
    }

    render()
    {
        return(
            <div>
                <div className='container'>
                    <div className='form-div'>
                        <form onSubmit={this.onSubmit}>
                            <input type = 'text'
                                   placeholder = 'Name'
                                   onChange = {this.changeName}
                                   value = {this.state.name}
                                   className = 'form-control form-group'
                            />

                            <input type = 'text'
                                   placeholder = 'Last Name'
                                   onChange = {this.changeLastName}
                                   value = {this.state.lastName}
                                   className = 'form-control form-group'
                            />

                            <input type = 'text'
                                   placeholder = 'Sex'
                                   onChange = {this.changeSex}
                                   value = {this.state.sex}
                                   className = 'form-control form-group'
                            />

                            <input type = 'text'
                                   placeholder = 'Email'
                                   onChange = {this.changeEmail}
                                   value = {this.state.email}
                                   className = 'form-control form-group'
                            />

                            <input type = 'password'
                                   placeholder = 'Password'
                                   onChange = {this.changePassword}
                                   value = {this.state.password}
                                   className = 'form-control form-group'
                            />

                            <input type='submit' className='btn btn-danger btn-block'/>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export {SignUp};
